// -------------------------------------------------------------------------------------------------------------------------------------------------------//
$(document).ready(function(){   //JQuery magic

$("#br-news-tabs").tabs();

// Publication Calendar
var cs_date;
$.ajax({async:false,dataType:'json',url:'/templates/javascript/calendar-items.json',success: function(d){cs_date=d;}});
$("#IssueCalendar").datepicker({
  beforeShowDay: function(d){
    var date = $.datepicker.formatDate("yy-mm-dd",d);
    var link = "/?tpl=163&date="+date;
    show=false;style="";caption="";
    if (cs_date.issue[date]){
      show=true;style="issue";caption="Выпуск "+cs_date.issue[date].title;
    }
    return [show,style,caption,link];
  }
});


$("#f_search_start_date").datepicker();
$("#f_search_end_date").datepicker();

// Print issue fancybox plugin
$("a[rel=lightbox]").fancybox({type:'image',padding:0,hideOnContentClick:'true',hideOnOverlayClick:'true',transitionIn:'elastic','transitionOut':'elastic',centerOnScroll:true,swf:{wmode:'transparent'}});

// Poll form ajax implementation
$('form[name=poll]').submit(function(){
  var url = $('form[name=poll]').attr("action");
  var form = $('form[name=poll]').serialize();
  $.post(url,form,function(data){$('#poll').html(data);});
  return false;
});


// Font size increase / decrease
$("a.fnt").click(function(){$("#sc_fnt").toggle();return false;});
var articleBox = $("#article-full *");
var originalFontSize = $(articleBox).css('font-size');
// Reset Font Size
$(".resetFont").click(function(){$(articleBox).css('font-size', originalFontSize);return false;});
// Increase Font Size
$(".increaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*1.1;
  $(articleBox).css('font-size', newFontSize);
  return false;
});
// Decrease Font Size
$(".decreaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*0.9;
  $(articleBox).css('font-size', newFontSize);
  return false;
});


}); //end of JQuery magic