// -------------------------------------------------------------------------------------------------------------------------------------------------------//
$(document).ready(function(){   //JQuery magic

// Block UI
$.blockUI.defaults.message='<img src="data:image/gif;base64," width="31" height="31" alt="Загрузка..." />';
$.blockUI.defaults.css.border='none';
$.blockUI.defaults.css.backgroundColor='transparent';
$.blockUI.defaults.overlayCSS.backgroundColor='#fff';
$.blockUI.defaults.overlayCSS.opacity=0.8;

// Photo Gallery (PWI plugin)
$("#photo_gallery_single").pwi({
  username: 'brestskayagazeta',
  maxResults: 1,
//  mode: 'latest',
  thumbSize: 144,
  album: 'YzVEcI',
  thumbCss: {'width':'140px', 'height':'140px', 'overflow':'hidden'}
});
$("#photo_gallery").pwi({
  username: 'brestskayagazeta',
  maxResults: 24,
  albumThumbSize: 144,
  thumbSize: 144
});
$('#photo_gallery img').live('mouseenter',function(){$(this).animate({opacity:1},200);});
$('#photo_gallery img').live('mouseleave',function(){$(this).animate({opacity:0.7},200);});

// Print issue fancybox plugin
$("a[rel=lightbox]").fancybox({type:'image',padding:0,hideOnContentClick:'true',hideOnOverlayClick:'true',transitionIn:'elastic','transitionOut':'elastic',centerOnScroll:true});

// Popups based on fancybox
$("a.email").fancybox({type:'iframe',width:370,height:410,transitionIn:'none',transitionOut:'none'});
$("a.send").fancybox({type:'iframe',width:310,height:340,transitionIn:'none',transitionOut:'none'});
$("a[name=poll-results]").fancybox({transitionIn:'none',transitionOut:'none'});

$('.minigal_thumb').fancybox({type:'image',titlePosition:'inside'});
$('.minigal_thumb').live('mouseenter',function(){$(this).animate({opacity:1},200);});
$('.minigal_thumb').live('mouseleave',function(){$(this).animate({opacity:0.7},200);});


// Poll form ajax implementation
$("#submit_poll").live("click", function(){
  $('#poll').block();
  var url = $('form[name=poll]').attr("action");
  var form = $('form[name=poll]').serialize();
  $.post(url,form,function(data){$('#poll').html(data);});
  return false;
});

// Search form calendars
$("#f_search_start_date").datepicker();
$("#f_search_end_date").datepicker();


// Font size increase / decrease
var articleBox = $(".article_text");
var originalFontSize = $(articleBox).css('font-size');
// Reset Font Size
$(".resetFont").click(function(){
  $(articleBox).css('font-size', originalFontSize);
  return false;
});
// Increase Font Size
$(".increaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*1.1;
  $(articleBox).css('font-size', newFontSize);
  return false;
});
// Decrease Font Size
$(".decreaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*0.9;
  $(articleBox).css('font-size', newFontSize);
  return false;
});

// Tab Main News - Featured Contentslider
if ($("#tabmn").length > 0){
  featuredcontentslider.init ({
    id: "tabmn",
    contentsource: ["inline", ""],
    toc: "markup",
    nextprev: ["", ""],
    revealtype: "mouseover",
    enablefade: [false, 0.2],
    autorotate: [true, 8000]
  });
}

// Фото в статьях
var cs_img = $(".cs_img");
$.each(cs_img, function(){
  switch ($(this).css('float')){
    case "left": $(this).css('margin', '0 10px 2px 0'); break;
    case "right": $(this).css('margin', '0 0 2px 10px'); break;
    default: $(this).css('margin', '0 auto');
  }
});

// Comments form EULA
$("a.eula").click(function(){$(".eula").toggle();});

$('.tabs-labels').delegate('li:not(.current)', 'click', function() {
	$(this).addClass('current').siblings().removeClass('current')
		.parents('.tabs').find('.tab').eq($(this).index()).show().siblings('.tab').hide();
	return false;
});

$('.slider').flexslider({
	selector: ".slides .slide",
	direction: "vertical",
	animation: "slide",
	slideshow: false,
	controlNav: false,
	prevText: "Предыдущие",
	nextText: "Следующие"
});

$('.slider .slide li').mouseenter(function(){
	var slideDescription = $(this).find('.slide-content').html();
	$(this).closest('.slider').parent().children('.big-slide').html(slideDescription);
});

$(".collapsible .title").click(function(){
	$(this).parent().toggleClass("expanded");
});

}); //end of JQuery magic