// Cookie array functions
var cookieArray = function(cookieName) {
var cookie = $.cookie(cookieName);
if (cookie){var temp = cookie.split(",");var items = new Array();for(var i in temp){i=temp[i].split(":");items[i[0]]=i[1];}}else{var items=new Array();}
return {
  "add": function(key,val) {items[key]=val;var res=new Array();for(var i in items){res.push(i+":"+items[i]);}$.cookie(cookieName,res,{expires:30,path:'/'});},
  "clear": function() {$.cookie(cookieName, null);items=new Array();},
  "get": function(val) {return items[val];}
}}

// Newsblocks initialization & cookie storage
var newsbox=new cookieArray("NewsBoxes");
$("div[id^='newsbox-']").each(function(i,n){
var t = newsbox.get($(n).attr("id"));
if (!t) t=3; // Number of extra items to show is set here
if (t==0){$(n).find("a[href='#remove']").css("opacity","0.2");}
if (t >= $(n).find("ul").children().length){$(n).find("a[href='#add']").css("opacity","0.2");}
$(n).find("ul").find("li:lt("+t+")").show();
newsbox.add($(n).attr("id"),$(n).find("ul").children(":visible").length);
});

// Newsblocks add button click event
$("a[href='#add']").click(function(){
if ($(this).css("opacity")!="0.2") {
  var box=$(this).parents("div [id^='newsbox-']");
  var i=$(box).find("ul");
  $(i).find("li:hidden:first").show();
  if ($(i).children(":hidden").length<1) $(this).css("opacity","0.2");
  if ($(i).children(":visible").length>0) $(this).siblings("a[href='#remove']").css("opacity","1");
  newsbox.add(box.attr("id"),$(i).children(":visible").length);
}return false;
});

// Newsblocks remove button click event
$("a[href='#remove']").click(function(){
if ($(this).css("opacity")!="0.2") {
  var box=$(this).parents("div [id^='newsbox-']");
  var i=$(box).find("ul");
  $(i).find("li:visible:last").hide();
  if ($(i).children(":visible").length<1) $(this).css("opacity","0.2");
  if ($(i).children(":hidden").length>0) $(this).siblings("a[href='#add']").css("opacity","1");
  newsbox.add(box.attr("id"),$(i).children(":visible").length);
}return false;
});

// Font size increase / decrease
var articleBox = $(".b-article_text");
var font_cookie = new cookieArray("FontSize");
var originalFontSize = $(articleBox).css('font-size');
if (font_cookie.get('font-size')) $(articleBox).css('font-size', font_cookie.get('font-size'));
// Reset Font Size
$(".resetFont").click(function(){
  $(articleBox).css('font-size', originalFontSize);
  font_cookie.add('font-size', $(articleBox).css('font-size'));
  return false;
});
// Increase Font Size
$(".increaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*1.1;
  $(articleBox).css('font-size', newFontSize);
  font_cookie.add('font-size', $(articleBox).css('font-size'));
  return false;
});
// Decrease Font Size
$(".decreaseFont").click(function(){
  var currentFontSize = $(articleBox).css('font-size');
  var currentFontSizeNum = parseFloat(currentFontSize, 10);
  var newFontSize = currentFontSizeNum*0.9;
  $(articleBox).css('font-size', newFontSize);
  font_cookie.add('font-size', $(articleBox).css('font-size'));
  return false;
});

