// Comments form ajax implementation
$('#comments_form .f-comments-submit-w a').live("click",function(){
  $('#comments_form').block();
  var url = $('#comments_form form[name=submit_comment]').attr("action")+"?tpl=article-comments-form.tpl";
  var comments_url = $('#comments_form form[name=submit_comment]').attr("action")+"?tpl=article-comments.tpl";
  var form = $('#comments_form form[name=submit_comment]').serialize();
  $.post(url, form, function(data){
    if ($(data).hasClass('post_this')) {
      $('#comments_form').remove();
      $("#comments").block().load(comments_url + " #comments_list"); }
    else {
      $('#comments_form').replaceWith(data);
      $("#comments_form #captcha").attr("src", "/include/captcha/image.php?"+Math.floor(100*Math.random()));
    }
  });
  return false;
});

