$(document).ready(function(){   // jQuery magic

// Ticker on front page
$('#js-news').ticker({
	speed: 0.15,
	htmlFeed: true,
	pauseOnItems: 5000,
	fadeInSpeed: 0,
	fadeOutSpeed: 0,
	titleText: 'Последние новости'
});

// Photo scroller on front page
if ($("#photo-scrollable").length>0){
$("#photo-scrollable .c-photovideo_photo").pwi({
  username: 'inform.progulka',
  mode: 'latest',
  maxResults: 8,
  thumbSize: 250,
  scrollable: {
    enabled: true,
    page: 1,
    circular: true,
    mousewheel: true,
    navigator: {enabled: true, navi: "#photo-scrollable .navi"},
    autoscroll: {enabled: false, interval: 6000},
	showAlbumTitles: true,
	showPhotoCaption: true,
	popupExt: function(photos){
							photos.fancybox({
								'hideOnContentClick': false
							})
	},
  },
  fancybox_config: {titlePosition:'inside'}
});
}


$('#photo_gallery img').live('mouseenter',function(){$(this).animate({opacity:1},200);});
$('#photo_gallery img').live('mouseleave',function(){$(this).animate({opacity:0.7},200);});

// Readers photo and Article gallery fancybox popup
$(document).ready(function() {
$("a.enlarge").fancybox({
    'titleShow'   : true,
    'titlePosition' : 'inside',
    'transitionIn'  : 'elastic',
    'transitionOut' : 'elastic'
  });
});

// Increase/decrese font size on article page
var section = new Array('#article-itself','.pe-div','.pe-div p'); 
section = section.join(',');
// Reset Font Size
var originalFontSize = $(section).css('font-size');
$(".resetFont").click(function(){
  $(section).css('font-size', originalFontSize); 
});
// Increase Font Size
$(".incr").click(function(){
var currentFontSize = $(section).css('font-size');
var currentFontSizeNum = parseFloat(currentFontSize, 10);
var newFontSize = currentFontSizeNum*1.2;
$(section).css('font-size', newFontSize);
return false;
});
// Decrease Font Size
$(".decr").click(function(){
var currentFontSize = $(section).css('font-size');
var currentFontSizeNum = parseFloat(currentFontSize, 10);
var newFontSize = currentFontSizeNum*0.8;
$(section).css('font-size', newFontSize);
return false;
});

// NBRB exchange rates
$("#nbrb").load("/?tpl=nbrb.tpl",function(){
if ($(this).html()=="") $(this).replaceWith('Источник недоступен');
});

// Publication Calendar
var cs_date;
$.ajax({async:false,dataType:'json',url:'/templates/js/calendar-items.json',success: function(d){cs_date=d;}});
$("#PublicationCalendar").datepicker({
  beforeShowDay: function(d){
    var date = $.datepicker.formatDate("yy-mm-dd",d);
    var link = "/ru/page/archive?date="+date;
    show=false;style="";caption="";
    if ($.inArray(date,cs_date.article)!=-1){
      var x=0;
      $.grep(cs_date.article,function(n,i){if(n==date) x++;});
      show=true;style="ui-state-highlight active";caption="Знойдзена артыкулаў: "+x;
    }
    return [show,style,caption,link];
  }
});
}); // end of jQuery magic